// Code node: Generate invoices with TYPE, WORK TYPE, and Project Base handling

const current = $items('Edit Fields')[0].json;

const salaries = $items('Get Salary Details').map(i => i.json);
const projects = $items('Get Project Details').map(i => i.json);
const clients = $items('Get Client Details').map(i => i.json);
const invoices = $items('Get Invoice').map(i => i.json);

const clientMap = {};
for (const c of clients) {
  clientMap[String(c['CLIENT ID']).trim()] = c;
}

// --- Helpers ---
function formatDateISO(d) {
  return d.toISOString().split('T')[0];
}

// dd MMM yyyy (for INVOICE CREATED)
function formatDateDisplay(d) {
  return d.toLocaleDateString('en-GB',{day:'2-digit',month:'short',year:'numeric'});
}

// m/d/yyyy (for DUE DATE)
function formatDateMDY(d) {
  return d.toLocaleDateString('en-US',{month:'numeric',day:'numeric',year:'numeric'});
}

function formatCurrency(value) {
  return value === "" || value === null ? "" : `$${Number(value).toFixed(2)}`;
}

function getMonthBoundaries(year, month) {
  const start = new Date(year, month, 1);
  const end = new Date(year, month + 1, 0);
  return { start, end };
}

function getHalfOfMonth(dateStr) {
  const d = new Date(dateStr);
  return d.getDate() <= 15 ? 1 : 2;
}

function getWeekOfMonth(dateStr) {
  const d = new Date(dateStr);
  const firstDay = new Date(d.getFullYear(), d.getMonth(), 1);
  const day = d.getDate();
  return Math.ceil((day + firstDay.getDay()) / 7);
}

function countWeekdays(start, end) {
  let count = 0;
  const cur = new Date(start);
  while (cur <= end) {
    const day = cur.getDay();
    if (day >= 1 && day <= 5) count++;
    cur.setDate(cur.getDate() + 1);
  }
  return count;
}

function buildServiceRange(freq, currentDay) {
  const d = new Date(
    currentDay.slice(0,4),
    parseInt(currentDay.slice(4,6)) - 1,
    parseInt(currentDay.slice(6,8))
  );
  const { start: monthStart, end: monthEnd } = getMonthBoundaries(d.getFullYear(), d.getMonth());

  if (freq === 'Monthly') {
    return { start: monthStart, end: monthEnd };
  }

  if (freq === 'Bi Weekly') {
    if (d.getDate() <= 15) {
      return { start: monthStart, end: new Date(d.getFullYear(), d.getMonth(), 15) };
    } else {
      return { start: new Date(d.getFullYear(), d.getMonth(), 16), end: monthEnd };
    }
  }

  if (freq === 'Weekly') {
    const day = d.getDay();
    const diffToMonday = (day === 0 ? -6 : 1 - day);
    const weekStart = new Date(d);
    weekStart.setDate(d.getDate() + diffToMonday);
    const weekEnd = new Date(weekStart);
    weekEnd.setDate(weekStart.getDate() + 6);

    return { 
      start: weekStart < monthStart ? monthStart : weekStart, 
      end: weekEnd > monthEnd ? monthEnd : weekEnd 
    };
  }

  return { start: d, end: d };
}

function invoiceExists(invNum) {
  return invoices.some(inv => 
    String(inv['INVOICE NUMER'] || inv['INVOICE NUMBER'] || '').trim() === String(invNum).trim()
  );
}

function buildInvoiceNumber(freqOrProject, current, clientId, projectId=null) {
  if (freqOrProject === 'Monthly') {
    return `INV_${current.currentYearMonth}_${clientId}`;
  }
  if (freqOrProject === 'Weekly') {
    const wk = getWeekOfMonth(
      `${current.currentDay.slice(0,4)}-${current.currentDay.slice(4,6)}-${current.currentDay.slice(6,8)}`
    );
    return `INV_${current.currentYearMonth}_W${wk}_${clientId}`;
  }
  if (freqOrProject === 'Bi Weekly') {
    const half = getHalfOfMonth(
      `${current.currentDay.slice(0,4)}-${current.currentDay.slice(4,6)}-${current.currentDay.slice(6,8)}`
    );
    return `INV_${current.currentYearMonth}_H${half}_${clientId}`;
  }
  if (freqOrProject === 'Project') {
    return `INV_PROJ_${projectId}_${clientId}`;
  }
  return null;
}

function buildInvoiceJson(client, workType, type, description, invNum, rangeStart, rangeEnd, ratePerHour, workDailyHours, numberOfDays, totalHours, rate, amount, createdDate, dueDate, projectId="") {
  return {
    'INVOICE NUMBER': invNum,
    'INVOICE MONTH': rangeStart.toLocaleString('en-US',{month:'long'}),
    'STATUS': 'Pending Creation',
    'CLIENT ID': client['CLIENT ID'],
    'CLIENT NAME': client['NAME'],
    'COMPANY': client['COMPANY'],
    'EMAIL ADDRESS': client['EMAIL ADDRESS'],
    'ADDRESS': client['ADDRESS'],
    'WORK TYPE': workType,
    'PROJECT ID': workType === "Project Base" ? projectId : "",
    'TYPE': type,
    'DESCRIPTION OF SERVICE': description,
    'SERVICE RANGE': `${formatDateISO(rangeStart)} - ${formatDateISO(rangeEnd)}`,
    'RATE PER HOUR': ratePerHour,
    'WORK HOURS DAILY': workDailyHours,
    'NUMBER OF DAYS': numberOfDays,
    'TOTAL HOURS': totalHours,
    'RATE/PRICE': rate === "" ? "" : formatCurrency(rate),
    'AMOUNT': amount === "" ? "" : formatCurrency(amount),
    'SUB TOTAL': amount === "" ? "" : formatCurrency(amount),
    'TOTAL AMOUNT DUE': amount === "" ? "" : formatCurrency(amount),
    'INVOICE CREATED': formatDateDisplay(createdDate), // dd MMM yyyy
    'DUE DATE': formatDateMDY(dueDate),               // m/d/yyyy
    'NOTES': `Auto-generated invoice for ${client['NAME']} (${workType})`
  };
}

// --- Main ---
const results = [];
const today = new Date(
  current.currentDay.slice(0,4),
  parseInt(current.currentDay.slice(4,6)) - 1,
  parseInt(current.currentDay.slice(6,8))
);

// Handle Full Time / Part Time
for (const salary of salaries) {
  const freq = salary['PAY FREQUENCY'];
  if (!['Monthly', 'Weekly', 'Bi Weekly'].includes(freq)) continue;

  const clientId = String(salary['CLIENT ID']).trim();
  const client = clientMap[clientId];
  if (!client) continue;

  const workType = client['TYPE'] || "Full Time";

  const serviceRange = buildServiceRange(freq, current.currentDay);
  const dueDate = new Date(serviceRange.end);
  const createdDate = new Date(serviceRange.end);
  createdDate.setDate(serviceRange.end.getDate() - (salary['NOTICE INVOICE DAYS'] || 0));

  if (today < createdDate) continue;

  const invNum = buildInvoiceNumber(freq, current, clientId);
  if (!invNum) continue;

  if (!invoiceExists(invNum)) {
    const baseSalary = salary['SALARY'] || 0;
    const hourlyRate = parseFloat(salary['HOURLY RATE'] || 0);
    const workHours = parseFloat(salary['WORK HOURS DAILY'] || 0);

    let type = "FIXED";
    let numberOfDays = 1;
    let workDailyHours = "";
    let rate = baseSalary;
    let amount = baseSalary;
    let ratePerHour = "";
    let totalHours = "";
    let description = `${freq} Salary`;

    if (hourlyRate && workHours) {
      type = "HOURLY";
      numberOfDays = countWeekdays(serviceRange.start, serviceRange.end);
      workDailyHours = workHours;
      totalHours = numberOfDays * workHours;
      ratePerHour = formatCurrency(hourlyRate);
      rate = hourlyRate * workHours;
      amount = totalHours * hourlyRate;
      description = `${freq} Pay (Hourly Rate)`;
    } else {
      if (freq === 'Weekly') {
        const daysInMonth = serviceRange.end.getDate();
        const weeksInMonth = Math.ceil(daysInMonth / 7);
        amount = baseSalary / weeksInMonth;
      } else if (freq === 'Bi Weekly') {
        amount = baseSalary / 2;
      } else if (freq === 'Monthly') {
        amount = baseSalary;
      }
      numberOfDays = 1;
      workDailyHours = "";
      ratePerHour = "";
      totalHours = "";
      rate = amount;
    }

    results.push({ json: buildInvoiceJson(
      client, workType, type, description, invNum,
      serviceRange.start, serviceRange.end,
      ratePerHour, workDailyHours, numberOfDays, totalHours,
      rate, amount, createdDate, dueDate
    )});
  }
}

// Handle Project Base
for (const project of projects) {
  const clientId = String(project['CLIENT ID']).trim();
  const client = clientMap[clientId];
  if (!client) continue;

  const workType = "Project Base";
  const projectId = project['PROJECT ID'];

  const startDate = new Date(project['START DATE']);
  const endDate = new Date(project['END DATE']);
  const dueDate = new Date(endDate);
  dueDate.setDate(dueDate.getDate() + 3); // 3 days after project end
  const createdDate = today; // no Notice for projects

  const invNum = buildInvoiceNumber('Project', current, clientId, projectId);
  if (!invNum) continue;

  if (!invoiceExists(invNum)) {
    const hourlyRate = parseFloat(project['HOURLY RATE'] || 0);
    const totalHours = parseFloat(project['TOTAL HOURS'] || 0);
    const amount = project['SALARY'] || (hourlyRate * totalHours);

    results.push({ json: buildInvoiceJson(
      client, workType, "HOURLY", project['PROJECT TITLE'], invNum,
      startDate, endDate,
      formatCurrency(hourlyRate), "", "", totalHours,
      hourlyRate, amount, createdDate, dueDate, projectId
    )});
  }
}

return results;

